function Header({ todos, setTodos, name, setName, addTodo }) {

  const showDone = () => {
    setTodos(todos.filter(todo => todo.done))
    setDone(true)
  }

  const showUndone = () => {
    setTodos(todos.filter(todo => !todo.done))
    setDone(true)
  }

  return (
    <header>
      <div>
        <input className="add_todo_input" placeholder="Enter todo name" value={name} onChange={(e) => setName(e.target.value)}/>
        <button className="add_todo_button" onClick={addTodo}>Add</button>
      </div>
      <div className="filters">
        <a href="/">All</a>
        <a href="/">Done</a>
        <a href="/">To Do</a>
      </div>
    </header>
   );
}

export default Header;
