import { useEffect, useState } from "react";
import Todos from "./Components/Todos";
import Header from "./Components/Header";
import { ThemeProvider, createTheme } from "@mui/material";

function App() {

  const [todos, setTodos] = useState([])
  const [filteredTodos, setFilteredTodos] = useState([])
  const [value, setValue] = useState(0);
  const [isDarkMode, setIsDarkMode] = useState(false);

  useEffect(() => {
    if(value === 1) {
      setFilteredTodos(todos.filter(todo => todo.completed === true))
    } else if (value === 2) {
      setFilteredTodos(todos.filter(todo => todo.completed === false))
    } else {
      setFilteredTodos(todos)
    }}, [todos, value])

    const darkTheme = createTheme({
      palette: {
        mode: 'dark', // Set the mode to dark
      },
    });

    const toggleDarkMode = () => {
      setIsDarkMode((prev) => !prev);
    };

  return (
    <ThemeProvider theme={isDarkMode ? darkTheme : createTheme()}>
      <div className="App" style={isDarkMode ? {backgroundColor: "#121212"} : {}}>
        <button onClick={toggleDarkMode}>Toggle Dark Mode</button>
        <Header todos={todos} setTodos={setTodos} value={value} setValue={setValue}/>
        <Todos todos={todos} filteredTodos={filteredTodos} setTodos={setTodos}/>
      </div>
    </ThemeProvider>
  );
}

export default App;
