import { useEffect, useState, useContext, createContext } from "react";
import Todos from "./Components/Todos";
import Header from "./Components/Header";

function App() {

  const TodoContext = createContext()

  const [todos, setTodos] = useState([])
  const [filteredTodos, setFilteredTodos] = useState([])
  const [value, setValue] = useState(0);
  const [completed, setCompleted] = useState(true)

  useEffect(() => {
    if(value === 1) {
      setFilteredTodos(todos.filter(todo => todo.completed === true))
    } else if (value === 2) {
      setFilteredTodos(todos.filter(todo => todo.completed === false))
    } else {
      setFilteredTodos(todos.sort((a, b) => (a.completed === b.completed) ? 0 : a.completed ? 1 : -1))
    }

    if(todos.length > 0) {
      setCompleted(todos.every(todo => todo.completed === true))
    }
  }, [todos, value])

  return (
    <TodoContext.Provider todos={todos} filteredTodos={filteredTodos} setTodos={setTodos} value={value} setValue={setValue} completed={completed} setCompleted={setCompleted}>
      <div className="App">
        <Header todos={todos} setTodos={setTodos} value={value} setValue={setValue} completed={completed} setCompleted={setCompleted}/>
        <Todos todos={todos} filteredTodos={filteredTodos} setTodos={setTodos}/>
      </div>
    </TodoContext.Provider>
  );
}

export default App;
