import { useState } from "react";

function Todo({ todo, todos, setTodos }) {

  const [editingTodo, setEditingTodo] = useState(null)

  const editTodo = (id) => {
    setEditingTodo(id);
  };

  const saveEditedTodo = (id, newName) => {
    setTodos(todos.map(todo =>
      todo.id === id ? { ...todo, name: newName } : todo
    ))
  }

  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id))
  }

  const toggleTodoStatus = (id) => {
    setTodos(todos.map(todo => (
      todo.id === id ? { ...todo, completed: !todo.completed } : todo
    )))
  }

  return (
    <div>
      {editingTodo === todo.id ?
        <>
          <input value={todo.name} onChange={(e) => saveEditedTodo(todo.id, e.target.value)}></input>
          <button onClick={() => setEditingTodo(null)}>Save</button>
        </> :
        <>
          <p style={todo.completed ? {textDecoration: "line-through"}: {}}>{todo.name}</p>
          <button onClick={() => editTodo(todo.id)}>Edit</button>
          <button onClick={() => deleteTodo(todo.id)}>Delete</button>
          <button onClick={() => toggleTodoStatus(todo.id)}>{todo.completed ? "Uncomplete" : "Complete"}</button>
        </>
      }
    </div>
   );
}

export default Todo;
