import { IconButton, TextField, Tooltip } from "@mui/material";
import DeleteIcon from '@mui/icons-material/Delete';
import { useEffect, useState } from "react";
import Edit from "@mui/icons-material/Edit";
import Check from "@mui/icons-material/Check";
import CheckBoxOutlineBlank from "@mui/icons-material/CheckBoxOutlineBlank";
import CheckBox from "@mui/icons-material/CheckBox";

function Todo({ todo, todos, setTodos }) {

  const [editingTodo, setEditingTodo] = useState(null)
  const [width, setWitdh] = useState(window.innerWidth >= 430)

  useEffect(() => {
    const handleResize = () => {
      setWitdh(window.innerWidth >= 430);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };

  }, [])

  const editTodo = (id) => {
    setEditingTodo(id);
  };

  const saveEditedTodo = (id, newName) => {
    setTodos(todos.map(todo =>
      todo.id === id ? { ...todo, name: newName } : todo
    ))
  }

  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id))
  }

  const toggleTodoStatus = (id) => {
    setTodos(todos.map(todo => (
      todo.id === id ? { ...todo, completed: !todo.completed } : todo
    )))
  }

  const handleText = (name) => {
    if(width && name.length > 60) {
      return name.slice(0, 60) + ' ...'
    } else if (!width && name.length > 20) {
      return name.slice(0, 18) + ' ...'
    } else {
      return name
    }
  }

  return (
    <div className="todo">
      {editingTodo === todo.id ?
        <>
          <TextField
            label="Edit your todo"
            variant="outlined"
            size="small"
            value={todo.name}
            onChange={(e) => saveEditedTodo(todo.id, e.target.value)}
            style={{width: "550px"}}
          />
          <IconButton onClick={() => setEditingTodo(null)}>
            <Check/>
          </IconButton>
        </> :
        <>
          <Tooltip
            title={todo.name}
            placement="top-start"
            slotProps={{popper: {modifiers: [{name: 'offset',options: {offset: [0, -11],},},],},}}
          >
            <p
              className="todo_text"
              style={todo.completed ? {textDecoration: "line-through", opacity: "30%"}: {}}
            >
              {handleText()}
              {/* {(width && todo.name.length > 60) ? todo.name.slice(0, 60) + ' ...' : todo.name} */}
            </p>
          </Tooltip>
          <div className="todo_actions">
            <IconButton onClick={() => editTodo(todo.id)}>
              <Edit/>
            </IconButton>
            <IconButton onClick={() => deleteTodo(todo.id)}>
              <DeleteIcon/>
            </IconButton>
            <IconButton onClick={() => toggleTodoStatus(todo.id)}>
              {!todo.completed ? <CheckBoxOutlineBlank/> : <CheckBox/>}
            </IconButton>
          </div>
        </>
      }
    </div>
   );
}

export default Todo;
