import { useState } from "react";
import { FaPencilAlt, FaCheck, FaTimes, FaTrashAlt, FaSave } from 'react-icons/fa';

function Todo({ todo, todos, setTodos }) {

  const [editingTodo, setEditingTodo] = useState(null)

  const editTodo = (id) => {
    setEditingTodo(id);
  };

  const saveEditedTodo = (id, newName) => {
    setTodos(todos.map(todo =>
      todo.id === id ? { ...todo, name: newName } : todo
    ))
  }

  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id))
  }

  const toggleTodoStatus = (id) => {
    setTodos(todos.map(todo => (
      todo.id === id ? { ...todo, completed: !todo.completed } : todo
    )))
  }

  return (
    <div className="todo">
      {editingTodo === todo.id ?
        <>
          <input value={todo.name} onChange={(e) => saveEditedTodo(todo.id, e.target.value)}></input>
          <FaSave onClick={() => setEditingTodo(null)}/>
        </> :
        <>
          <p style={todo.completed ? {textDecoration: "line-through"}: {}}>{todo.name}</p>
          <FaPencilAlt onClick={() => editTodo(todo.id)}/>
          <FaTrashAlt onClick={() => deleteTodo(todo.id)}/>
          {!todo.completed ? <FaCheck onClick={() => toggleTodoStatus(todo.id)}/> : <FaTimes onClick={() => toggleTodoStatus(todo.id)}/>}
        </>
      }
    </div>
   );
}

export default Todo;
