import { useEffect, useState } from "react";

function App() {

  const [todos, setTodos] = useState([])
  const [todo, setTodo] = useState("")
  const [filteredTodos, setFilteredTodos] = useState([])

  useEffect(() => {
    setFilteredTodos(todos)
  }, [todos])

  const addTodo = (todo) => {

    const newTodo = {
      id: Date.now(),
      name: todo,
      completed: false
    }

    if(todo !== "") {
      setTodos([...todos, newTodo])
      setTodo("")
    }
  }

  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id))
  }

  const completeTodo = (id) => {
    setTodos(todos.map(todo => (
      todo.id === id ? { ...todo, completed: !todo.completed } : todo
    )))
  }

  const filterTodos = (filter) => {
    if (filter === 'done') {
      setFilteredTodos(todos.filter(todo => todo.completed === true))
    } else if (filter === "undone") {
      setFilteredTodos(todos.filter(todo => todo.completed === false))
    } else {
      setFilteredTodos(todos)
    }
  }

  return (
    <div className="App">
      <div>
        <input
          placeholder="What should be done?"
          value={todo}
          onChange={(e) => setTodo(e.target.value)}
        />
        <button onClick={() => addTodo(todo)}>Add</button>
        <button onClick={() => filterTodos("")}>All</button>
        <button onClick={() => filterTodos("done")}>Done</button>
        <button onClick={() => filterTodos("undone")}>Undone</button>
      </div>
      <div>
        {filteredTodos ? filteredTodos.map(todo => (
          <div key={todo.id} style={{display: "flex", gap: "20px", marginTop: "10px"}}>
            <p style={todo.completed ? {textDecoration: "line-through"}: {}}>{todo.name}</p>
            <button onClick={() => deleteTodo(todo.id)}>Delete</button>
            <button onClick={() => completeTodo(todo.id)}>{todo.completed ? "Uncomplete" : "Complete"}</button>
          </div>
        )) : <div>There are no Todos</div>}
      </div>
    </div>
  );
}

export default App;
