import { useState } from "react";
import Todo from "./Todo";

function Todos() {

  const [name, setName] = useState('')
  const [todos, setTodos] = useState([])

  console.log(todos);

  const addTodo = () => {
    if(name.length !== 0) {
      setTodos(todos => [...todos, name])
      setName('')
    } else {

    }
  }

  return (
    <div className="container">
      <input className="add_todo_input" placeholder="Enter todo name" value={name} onChange={(e) => setName(e.target.value)}/>
      <button className="add_todo_button" onClick={addTodo}>Add</button>
      {todos.map(todo => (
        <Todo todo={todo}/>
      ))}
    </div>
  );
}

export default Todos;
