import { useState } from "react";

function App() {

  const [todos, setTodos] = useState([{ id: Date.now(), name: "todo 1", completed: false}])
  const [todo, setTodo] = useState("")

  const addTodo = (todo) => {

    const newTodo = {
      id: Date.now(),
      name: todo,
      completed: false
    }

    setTodos([...todos, newTodo])
    setTodo("")
  }

  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id))
  }

  const completeTodo = (id) => {
    setTodos(todos.map(todo => (
      todo.id === id ? { ...todo, complete: true } : true
    )))
  }

  return (
    <div className="App">
      <div>
        <input
          placeholder="What should be done?"
          value={todo}
          onChange={(e) => setTodo(e.target.value)}
        />
        <button onClick={() => addTodo(todo)}>Add</button>
      </div>
      <div>
        {todos.map(todo => (
          <div key={todo.id} style={{display: "flex", gap: "20px", marginTop: "10px"}}>
            <p>{todo.name}</p>
            <button onClick={() => deleteTodo(todo.id)}>Delete</button>
            <button onClick={() => completeTodo(todo.id)}>{todo.completed ? "Uncomplete" : "Complete"}</button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
