function Header({ name, setName, addTodo, showDone, showUndone }) {

  return (
    <header>
      <div>
        <input className="add_todo_input" placeholder="What needs to be done?" value={name} onChange={(e) => setName(e.target.value)}/>
        <button className="add_todo_button" onClick={addTodo}>Add</button>
      </div>
      <div className="filters">
        <a href="/">All</a>
        <a href="/" onClick={showDone}>Done</a>
        <a href="/" onClick={showUndone}>To Do</a>
      </div>
    </header>
   );
}

export default Header;
