import { useState } from "react"
import { Button, Tab, Tabs, TextField } from "@mui/material"

function Header ({ todos, setTodos, value, setValue }) {

  const [todo, setTodo] = useState("")
  const [alert, setAlert] = useState(false)

  const addTodo = (todo) => {

    const newTodo = {
      id: Date.now(),
      name: todo,
      completed: false
    }

    if(todo !== "") {
      setTodos([...todos, newTodo])
      setTodo("")
    } else {
      setAlert(prev => !prev)
    }
  }

  const handleInoputChange = (e) => {
    setTodo(e.target.value)
    setAlert(false)
  }

  return (
    <header className="flex flex-col items-center justify-center gap-5 sticky top-10 z-10">
      <div className="flex gap-5 max-h-14 w-full">
        <TextField
          error={alert && true}
          helperText={alert && "You can't leave the field empty."}
          label="What should be done?"
          variant="outlined"
          value={todo}
          onChange={(e) => handleInoputChange(e)}
          onKeyDown={(e) => e.key === 'Enter' && addTodo(todo)}
          className="m-w-xl w-full"
        />
        <Button
          variant="contained"
          onClick={() => addTodo(todo)}
        >
          Add
        </Button>
      </div>
      <Tabs value={value} onChange={(e, val) => setValue(val)}>
        <Tab label="all" />
        <Tab label="completed" />
        <Tab label="active" />
      </Tabs>
    </header>
   );
}

export default Header ;
