import { useEffect, useState } from "react";
import Todos from "./Components/Todos";
import Header from "./Components/Header";

function App() {

  const [todos, setTodos] = useState([])
  const [filteredTodos, setFilteredTodos] = useState([])
  const [value, setValue] = useState(0);

  useEffect(() => {
    if(value === 1) {
      setFilteredTodos(todos.filter(todo => todo.completed === true))
    } else if (value === 2) {
      setFilteredTodos(todos.filter(todo => todo.completed === false))
    } else {
      setFilteredTodos(todos.sort((a, b) => (a.completed === b.completed) ? -1 : a.completed ? 0 : 1))
    }}, [todos, value])

  return (
    <div className="App">
      <Header todos={todos} setTodos={setTodos} value={value} setValue={setValue}/>
      <Todos todos={todos} filteredTodos={filteredTodos} setTodos={setTodos}/>
    </div>
  );
}

export default App;
