import { useState } from "react";
import { IconButton, TextField } from "@mui/material";
import DeleteIcon from '@mui/icons-material/Delete';
import Edit from "@mui/icons-material/Edit";
import Check from "@mui/icons-material/Check";
import CheckBoxOutlineBlank from "@mui/icons-material/CheckBoxOutlineBlank";
import CheckBox from "@mui/icons-material/CheckBox";
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';

function Todo({ todo, todos, setTodos }) {

  const [editingTodo, setEditingTodo] = useState(null)
  const [expand, setExpand] = useState(false)
  const [textLength, setTextLength] = useState(60)

  const editTodo = (id) => {
    setEditingTodo(id);
  };

  const saveEditedTodo = (id, newName) => {
    setTodos(todos.map(todo =>
      todo.id === id ? { ...todo, name: newName } : todo
    ))
  }

  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id))
  }

  const toggleTodoStatus = (id) => {
    setTodos(todos.map(todo => (
      todo.id === id ? { ...todo, completed: !todo.completed } : todo
    )))
  }

  const expandText = () => {
    setExpand(expand => !expand)
  }

  return (
    <div className="flex items-center justify-between mb-2 px-6 py-2 bg-[#fafbfc] border-[1px] border-solid border-[#EEF2F6] rounded-2xl hover:bg-[#EFF7FF] hover:border-[#66B2FF]">
      {editingTodo === todo.id ?
        <>
          <TextField
            label="Edit your todo"
            variant="standard"
            size="small"
            value={todo.name}
            onChange={(e) => saveEditedTodo(todo.id, e.target.value)}
            style={{width: "550px"}}
          />
          <IconButton onClick={() => setEditingTodo(null)}>
            <Check/>
          </IconButton>
        </> :
        <>
          <p
            className={!expand ? "m-w-md overflow-hidden text-ellipsis" : "m-w-md break-all"}
            style={todo.completed ? {textDecoration: "line-through", opacity: "30%"}: {}}
          >
            {todo.name}
          </p>
          <div className={`min-w-40 text-right sm:${() => setTextLength(20)}`}>
            {todo.name.length > textLength && <IconButton onClick={expandText}>
              {!expand ? <ExpandMoreIcon/> : <ExpandLessIcon/>}
            </IconButton>}
            <IconButton onClick={() => editTodo(todo.id)}>
              <Edit/>
            </IconButton>
            <IconButton onClick={() => deleteTodo(todo.id)}>
              <DeleteIcon/>
            </IconButton>
            <IconButton onClick={() => toggleTodoStatus(todo.id)}>
              {!todo.completed ? <CheckBoxOutlineBlank/> : <CheckBox/>}
            </IconButton>
          </div>
        </>
      }
    </div>
   );
}

export default Todo;
