import { IconButton } from "@mui/material";
import DeleteIcon from '@mui/icons-material/Delete';
import { useState } from "react";
import { FaSave } from 'react-icons/fa';
import Edit from "@mui/icons-material/Edit";
import Done from "@mui/icons-material/Done";
import Undo from "@mui/icons-material/Undo";

function Todo({ todo, todos, setTodos }) {

  const [editingTodo, setEditingTodo] = useState(null)

  const editTodo = (id) => {
    setEditingTodo(id);
  };

  const saveEditedTodo = (id, newName) => {
    setTodos(todos.map(todo =>
      todo.id === id ? { ...todo, name: newName } : todo
    ))
  }

  const deleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id))
  }

  const toggleTodoStatus = (id) => {
    setTodos(todos.map(todo => (
      todo.id === id ? { ...todo, completed: !todo.completed } : todo
    )))
  }

  return (
    <div className="todo">
      {editingTodo === todo.id ?
        <>
          <input value={todo.name} onChange={(e) => saveEditedTodo(todo.id, e.target.value)}></input>
          <FaSave onClick={() => setEditingTodo(null)}/>
        </> :
        <>
          <p style={todo.completed ? {textDecoration: "line-through"}: {}}>{todo.name}</p>
          <div className="todo_actions">
            <IconButton onClick={() => editTodo(todo.id)}>
              <Edit />
            </IconButton>
            <IconButton onClick={() => deleteTodo(todo.id)}>
              <DeleteIcon />
            </IconButton>
            <IconButton onClick={() => toggleTodoStatus(todo.id)}>
              {!todo.completed ? <Done/> : <Undo/>}
            </IconButton>
          </div>
        </>
      }
    </div>
   );
}

export default Todo;
