import { useState } from "react";

function App() {

  const [todos, setTodos] = useState(
    [
      {
        id: Date.now(),
        name: "todo 1"
      }
    ]
  )
  const [todo, setTodo] = useState("")

  const addTodo = (todo) => {
    setTodos([...todos, todo])
    setTodo("")
  }

  return (
    <div className="App">
      <div>
        <input
          placeholder="What should be done?"
          value={todo}
          onChange={(e) => setTodo(e.target.value)}
        />
        <button onClick={() => addTodo(todo)}>Add</button>
      </div>
      <div>
        {todos.map(todo => (
          <p>{todo.name}</p>
        ))}
      </div>
    </div>
  );
}

export default App;
